import React, { useState } from "react";
import axios from "axios";
import { GoogleGenerativeAI } from "@google/generative-ai";

const genAI = new GoogleGenerativeAI("AIzaSyAqmfslqSGlrqWbSllhR5ce0NPD2hxMuGs");
const model = genAI.getGenerativeModel({ model: "gemini-pro" });
const model_vision = genAI.getGenerativeModel({ model: "gemini-pro-vision" });

const UploadAndDisplayImage = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState("");

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setSelectedImage(file);
    setLoading(true);

    // Prepare the file for sending to the API
    const formData = new FormData();
    formData.append("file", file);

    // Specify the API endpoint and your API key
    const apiEndpoint = "YOUR_GEMINI_API_ENDPOINT";
    const apiKey = "YOUR_API_KEY";

    // Prepare the API request
    const config = {
      headers: {
        Authorization: `Bearer ${apiKey}`,
        "Content-Type": "multipart/form-data",
      },
    };

    try {
      // Call the API with the image and the prompt
      const prompt = "give me recipe using the food in the picture";
      const result = await axios.post(apiEndpoint, formData, config);

      // Assuming the API returns JSON response with the text in a field named 'text'
      const text = result.data.text;
      setResponse(text);
    } catch (error) {
      console.error("Error uploading image and fetching data:", error);
      setResponse("Failed to fetch data");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Upload Your Refrigerator</h1>

      <input type="file" name="myImage" onChange={handleImageUpload} />

      <br />
      <br />

      {selectedImage && (
        <div>
          <img
            alt="not found"
            width={"250px"}
            src={URL.createObjectURL(selectedImage)}
          />
          <br />
          <button onClick={() => setSelectedImage(null)}>Remove</button>
        </div>
      )}

      {response && (
        <div>
          <p>Response from API:</p>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
};

export default UploadAndDisplayImage;
